import { createStore } from 'vuex';
import { auth, db } from '../firebase';

export default createStore({
  state: {
    user: null,
    studyGroups: [],
  },
  mutations: {
    setUser(state, user) {
      state.user = user;
    },
    setStudyGroups(state, studyGroups) {
      state.studyGroups = studyGroups;
    },
  },
  actions: {
    async fetchStudyGroups({ commit }) {
      try {
        const snapshot = await db.collection('studyGroups').get();
        const studyGroups = snapshot.docs.map((doc) => ({
          id: doc.id,
          ...doc.data(),
        }));
        commit('setStudyGroups', studyGroups);
      } catch (error) {
        console.error('Error fetching study groups:', error);
      }
    },
    async login({ commit }, { email, password }) {
      try {
        const userCredential = await auth.signInWithEmailAndPassword(
          email,
          password
        );
        commit('setUser', userCredential.user);
      } catch (error) {
        console.error('Error logging in:', error);
      }
    },
    async register({ commit }, { email, password }) {
      try {
        const userCredential = await auth.createUserWithEmailAndPassword(
          email,
          password
        );
        commit('setUser', userCredential.user);
      } catch (error) {
        console.error('Error registering:', error);
      }
    },
    async logout({ commit }) {
      try {
        await auth.signOut();
        commit('setUser', null);
      } catch (error) {
        console.error('Error logging out:', error);
      }
    },
  },
  modules: {},
});
